import React, { useState, useEffect } from 'react';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip,
  ResponsiveContainer, ScatterChart, Scatter, AreaChart, Area
} from 'recharts';
import { RefreshCw, AlertTriangle, TrendingUp, Shield } from 'lucide-react';

const ZOBONDashboard = () => {
  const [brandData, setBrandData] = useState([]);
  const [alertsData, setAlertsData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Mock data for demonstration
  const mockBrandData = [
    { brand: 'TechCorp', avg_trust_score: 7.5, mention_count: 1200, last_mention: '2025-07-01' },
    { brand: 'GreenEnergy', avg_trust_score: 8.2, mention_count: 800, last_mention: '2025-07-03' },
    { brand: 'FinanceFirst', avg_trust_score: 6.8, mention_count: 950, last_mention: '2025-07-02' },
    { brand: 'HealthPlus', avg_trust_score: 8.9, mention_count: 650, last_mention: '2025-07-04' },
    { brand: 'EduTech', avg_trust_score: 7.1, mention_count: 1100, last_mention: '2025-06-30' }
  ];

  const mockAlertsData = [
    { brand: 'TechCorp', bias_type: 'Urban Bias', alert_level: 'HIGH', created_at: '2025-07-01' },
    { brand: 'TechCorp', bias_type: 'Economic Bias', alert_level: 'MEDIUM', created_at: '2025-07-02' },
    { brand: 'GreenEnergy', bias_type: 'Regional Bias', alert_level: 'LOW', created_at: '2025-07-03' },
    { brand: 'FinanceFirst', bias_type: 'Linguistic Bias', alert_level: 'HIGH', created_at: '2025-07-01' },
    { brand: 'HealthPlus', bias_type: 'Urban Bias', alert_level: 'MEDIUM', created_at: '2025-07-04' },
    { brand: 'EduTech', bias_type: 'Economic Bias', alert_level: 'LOW', created_at: '2025-06-30' }
  ];

  const fetchBrandData = async () => {
    try {
      await new Promise(resolve => setTimeout(resolve, 1000));
      setBrandData(mockBrandData);
    } catch (err) {
      console.error('Error fetching brand data:', err);
      setError(err.message);
      setBrandData([]);
    }
  };

  const fetchAlertsData = async () => {
    try {
      await new Promise(resolve => setTimeout(resolve, 1000));
      setAlertsData(mockAlertsData);
    } catch (err) {
      console.error('Error fetching alerts data:', err);
      setError(err.message);
      setAlertsData([]);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      await Promise.all([fetchBrandData(), fetchAlertsData()]);
      setLoading(false);
    };
    fetchData();
  }, []);

  const getBubbleChartData = () => {
    return brandData.map(brand => {
      const daysSinceLastMention = Math.floor(
        (new Date() - new Date(brand.last_mention)) / (1000 * 60 * 60 * 24)
      );
      return {
        x: Number(brand.avg_trust_score) || 0,
        y: Number(brand.mention_count) || 0,
        z: Math.max(100 - daysSinceLastMention * 10, 20),
        name: String(brand.brand) || 'Unknown',
        trust_score: Number(brand.avg_trust_score) || 0,
        mentions: Number(brand.mention_count) || 0,
        recency: Number(daysSinceLastMention) || 0
      };
    });
  };

  const getBiasDistributionData = () => {
    const brandBiasMap = {};

    brandData.forEach(brand => {
      const brandName = String(brand.brand);
      brandBiasMap[brandName] = {
        brand: brandName,
        'Urban Bias': 0,
        'Economic Bias': 0,
        'Linguistic Bias': 0,
        'Regional Bias': 0
      };
    });

    alertsData.forEach(alert => {
      const brandName = String(alert.brand);
      const biasType = String(alert.bias_type);
      if (brandBiasMap[brandName] && brandBiasMap[brandName][biasType] !== undefined) {
        brandBiasMap[brandName][biasType] += 1;
      }
    });

    return Object.values(brandBiasMap);
  };

  const getTimelineData = () => {
    const timelineMap = {};

    alertsData.forEach(alert => {
      const date = new Date(alert.created_at).toISOString().split('T')[0];
      const alertLevel = String(alert.alert_level);

      if (!timelineMap[date]) {
        timelineMap[date] = { date: date, HIGH: 0, MEDIUM: 0, LOW: 0 };
      }

      if (timelineMap[date][alertLevel] !== undefined) {
        timelineMap[date][alertLevel] += 1;
      }
    });

    return Object.values(timelineMap).sort((a, b) => new Date(a.date) - new Date(b.date));
  };

  const getRiskMatrixData = () => {
    const brandRiskMap = {};

    brandData.forEach(brand => {
      const brandName = String(brand.brand);
      brandRiskMap[brandName] = {
        brand: brandName,
        alertCount: 0,
        totalSeverity: 0,
        avgSeverity: 0
      };
    });

    alertsData.forEach(alert => {
      const brandName = String(alert.brand);
      const alertLevel = String(alert.alert_level);

      if (brandRiskMap[brandName]) {
        brandRiskMap[brandName].alertCount += 1;
        const severityScore = alertLevel === 'HIGH' ? 3 : alertLevel === 'MEDIUM' ? 2 : 1;
        brandRiskMap[brandName].totalSeverity += severityScore;
      }
    });

    Object.values(brandRiskMap).forEach(brand => {
      brand.avgSeverity = brand.alertCount > 0
        ? Number((brand.totalSeverity / brand.alertCount).toFixed(2))
        : 0;
    });

    return Object.values(brandRiskMap);
  };

  // Simple tooltip that returns strings only
  const renderCustomTooltip = (props) => {
    const { active, payload, label } = props;

    if (!active || !payload || payload.length === 0) {
      return null;
    }

    const data = payload[0]?.payload;
    if (!data) {
      return null;
    }

    return (
      <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
        <p className="font-semibold">{data.name || data.brand || label || 'Unknown'}</p>
        {payload.map((entry, index) => (
          <p key={index} style={{ color: entry.color }}>
            {entry.dataKey}: {entry.value}
          </p>
        ))}
      </div>
    );
  };

  // Bubble chart specific tooltip
  const renderBubbleTooltip = (props) => {
    const { active, payload } = props;

    if (!active || !payload || payload.length === 0) {
      return null;
    }

    const data = payload[0]?.payload;
    if (!data) {
      return null;
    }

    return (
      <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
        <p className="font-semibold">{data.name}</p>
        <p>Trust Score: {data.trust_score.toFixed(2)}</p>
        <p>Mentions: {data.mentions.toLocaleString()}</p>
        <p>Days since last mention: {data.recency}</p>
      </div>
    );
  };

  // Risk matrix specific tooltip
  const renderRiskTooltip = (props) => {
    const { active, payload } = props;

    if (!active || !payload || payload.length === 0) {
      return null;
    }

    const data = payload[0]?.payload;
    if (!data) {
      return null;
    }

    return (
      <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
        <p className="font-semibold">{data.brand}</p>
        <p>Alert Count: {data.alertCount}</p>
        <p>Avg Severity: {data.avgSeverity}</p>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-96">
        <RefreshCw className="animate-spin h-8 w-8 text-blue-500" />
        <span className="ml-2 text-gray-600">Loading ZOBON Analytics...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 bg-gray-50 min-h-screen">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">ZOBON Analytics Dashboard</h1>
          <p className="text-gray-600">सत्य • न्याय • सबका साथ | Ethical Marketing Intelligence</p>
        </div>
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-red-700 mb-2">Connection Error</h2>
          <p className="text-red-600 mb-4">Unable to connect to ZOBON API backend</p>
          <p className="text-gray-600 text-sm mb-4">Error: {error}</p>
          <button
            onClick={() => {
              setError(null);
              setLoading(true);
              Promise.all([fetchBrandData(), fetchAlertsData()]).then(() => setLoading(false));
            }}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center mx-auto"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry Connection
          </button>
        </div>
      </div>
    );
  }

  if (brandData.length === 0 && alertsData.length === 0) {
    return (
      <div className="p-6 bg-gray-50 min-h-screen">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">ZOBON Analytics Dashboard</h1>
          <p className="text-gray-600">सत्य • न्याय • सबका साथ | Ethical Marketing Intelligence</p>
        </div>
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
          <AlertTriangle className="h-12 w-12 text-yellow-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-yellow-700 mb-2">No Data Available</h2>
          <p className="text-yellow-600 mb-4">No brand or alert data found from the API</p>
          <button
            onClick={() => {
              setError(null);
              setLoading(true);
              Promise.all([fetchBrandData(), fetchAlertsData()]).then(() => setLoading(false));
            }}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center mx-auto"
          >
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh Data
          </button>
        </div>
      </div>
    );
  }

  const bubbleData = getBubbleChartData();
  const biasData = getBiasDistributionData();
  const timelineData = getTimelineData();
  const riskData = getRiskMatrixData();

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">ZOBON Analytics Dashboard</h1>
        <p className="text-gray-600">सत्य • न्याय • सबका साथ | Ethical Marketing Intelligence</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Chart 1: Trust Score vs Engagement */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center mb-4">
            <TrendingUp className="h-5 w-5 text-blue-500 mr-2" />
            <h2 className="text-xl font-semibold text-gray-900">Trust Score vs Engagement</h2>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <ScatterChart data={bubbleData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="x" type="number" name="Trust Score" />
              <YAxis dataKey="y" type="number" name="Mentions" />
              <Tooltip content={renderBubbleTooltip} />
              <Scatter dataKey="z" fill="#3b82f6" />
            </ScatterChart>
          </ResponsiveContainer>
        </div>

        {/* Chart 2: Bias Distribution by Brand */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center mb-4">
            <AlertTriangle className="h-5 w-5 text-red-500 mr-2" />
            <h2 className="text-xl font-semibold text-gray-900">Bias Distribution by Brand</h2>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={biasData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="brand" angle={-45} textAnchor="end" height={80} />
              <YAxis />
              <Tooltip content={renderCustomTooltip} />
              <Bar dataKey="Urban Bias" stackId="a" fill="#8b5cf6" />
              <Bar dataKey="Economic Bias" stackId="a" fill="#f59e0b" />
              <Bar dataKey="Linguistic Bias" stackId="a" fill="#06b6d4" />
              <Bar dataKey="Regional Bias" stackId="a" fill="#10b981" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Chart 3: Alert Severity Timeline */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center mb-4">
            <Shield className="h-5 w-5 text-green-500 mr-2" />
            <h2 className="text-xl font-semibold text-gray-900">Alert Severity Timeline</h2>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <AreaChart data={timelineData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis />
              <Tooltip content={renderCustomTooltip} />
              <Area type="monotone" dataKey="HIGH" stackId="1" stroke="#ef4444" fill="#ef4444" />
              <Area type="monotone" dataKey="MEDIUM" stackId="1" stroke="#f59e0b" fill="#f59e0b" />
              <Area type="monotone" dataKey="LOW" stackId="1" stroke="#10b981" fill="#10b981" />
            </AreaChart>
          </ResponsiveContainer>
        </div>

        {/* Chart 4: Brand Risk Matrix */}
        <div className="bg-white p-6 rounded-lg shadow-lg">
          <div className="flex items-center mb-4">
            <AlertTriangle className="h-5 w-5 text-orange-500 mr-2" />
            <h2 className="text-xl font-semibold text-gray-900">Brand Risk Matrix</h2>
          </div>
          <ResponsiveContainer width="100%" height={300}>
            <ScatterChart data={riskData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="alertCount" type="number" name="Alert Count" />
              <YAxis dataKey="avgSeverity" type="number" domain={[0, 3]} name="Avg Severity" />
              <Tooltip content={renderRiskTooltip} />
              <Scatter dataKey="avgSeverity" fill="#f59e0b" />
            </ScatterChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default ZOBONDashboard;
