import React, { useEffect, useState } from 'react';
import dayjs from 'dayjs';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  ScatterChart,
  Scatter,
  LineChart,
  Line,
  Cell,
} from 'recharts';

// Eco-friendly color palette inspired by nature
const ECO_COLORS = [
  '#2E7D32', // Deep Forest Green
  '#4CAF50', // Primary Green
  '#81C784', // Light Green
  '#A5D6A7', // Sage Green
  '#66BB6A', // Medium Green
  '#388E3C', // Dark Green
  '#4DB6AC', // Teal Green
  '#26A69A', // Aqua Green
  '#00897B', // Deep Teal
  '#009688', // Teal
  '#8BC34A', // Light Lime
  '#689F38', // Olive Green
];

const ECO_GRADIENT_COLORS = [
  { start: '#4CAF50', end: '#2E7D32' },
  { start: '#81C784', end: '#4CAF50' },
  { start: '#A5D6A7', end: '#66BB6A' },
  { start: '#4DB6AC', end: '#26A69A' },
  { start: '#8BC34A', end: '#689F38' },
  { start: '#00897B', end: '#009688' },
];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Simulate API calls - replace with your actual API service
        const [brandsResponse, alertsResponse] = await Promise.all([
          fetch('http://localhost:5001/api/brands').then(res => res.json()),
          fetch('http://localhost:5001/api/alerts?limit=500').then(res => res.json())
        ]);

        setBrands(brandsResponse || []);
        setAlerts(alertsResponse || []);
      } catch (err) {
        setError('Failed to load dashboard data');
        console.error('Dashboard error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Enhanced eco-themed tooltip component
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-white/95 p-4 rounded-xl shadow-lg border border-green-200 backdrop-blur-sm">
          <p className="font-bold text-green-800 mb-2 text-sm">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-xs font-medium mb-1 flex items-center" style={{ color: entry.color }}>
              <span className="inline-block w-2 h-2 rounded-full mr-2" style={{ backgroundColor: entry.color }}></span>
              {`${entry.name}: ${typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  // 1. Avg Trust Score by Brand
  const trustData = brands.map(({ brand, avg_trust_score }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
  }));

  // 2. Scatter Chart: brand mentions vs avg trust score
  const scatterData = brands.map(({ brand, avg_trust_score, mention_count }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
    mention_count,
  }));

  // 3. Stacked Bar Chart: Alerts by Brand & Level
  const alertLevels = Array.from(new Set(alerts.map(a => a.alert_level)));
  const brandAlertLevelMap = {};
  brands.forEach(({ brand }) => {
    brandAlertLevelMap[brand] = {};
  });
  alerts.forEach(({ brand, alert_level }) => {
    if (brandAlertLevelMap[brand]) {
      brandAlertLevelMap[brand][alert_level] = (brandAlertLevelMap[brand][alert_level] || 0) + 1;
    }
  });
  const stackedBarData = brands.map(({ brand }) => {
    const item = { brand };
    alertLevels.forEach(level => {
      item[level] = brandAlertLevelMap[brand][level] || 0;
    });
    return item;
  });

  // 4. Trust Score Trend Line Chart
  const brandDateMap = {};
  alerts.forEach(({ brand, trust_score, timestamp }) => {
    if (!brand || trust_score == null || !timestamp) return;
    const date = dayjs(timestamp).format('YYYY-MM-DD');
    const key = `${brand}-${date}`;
    if (!brandDateMap[key]) brandDateMap[key] = { brand, date, trustScores: [] };
    brandDateMap[key].trustScores.push(trust_score);
  });
  const trendData = Object.values(brandDateMap).map(({ brand, date, trustScores }) => ({
    brand,
    date,
    avgTrustScore: trustScores.reduce((a, b) => a + b, 0) / trustScores.length,
  }));
  const uniqueDates = Array.from(new Set(trendData.map(d => d.date))).sort();
  const uniqueBrands = Array.from(new Set(trendData.map(d => d.brand)));
  const lineChartData = uniqueDates.map(date => {
    const entry = { date };
    uniqueBrands.forEach(brand => {
      const found = trendData.find(d => d.brand === brand && d.date === date);
      entry[brand] = found ? Number(found.avgTrustScore.toFixed(2)) : null;
    });
    return entry;
  });

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-white flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin w-12 h-12 border-4 border-green-500 border-t-transparent rounded-full mx-auto mb-4"></div>
          <p className="text-green-700 font-medium">Loading sustainable analytics...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-white flex items-center justify-center">
        <div className="text-center p-8">
          <div className="text-red-500 text-6xl mb-4">🌱</div>
          <h2 className="text-xl font-bold text-green-800 mb-2">Oops! Something went wrong</h2>
          <p className="text-green-600 mb-4">{error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 transition-colors"
          >
            🔄 Refresh Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 via-white to-green-50 relative overflow-hidden">
      {/* Nature-inspired background elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-green-100/40 to-green-200/40 rounded-full blur-3xl"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-br from-green-200/40 to-green-300/40 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-gradient-to-br from-green-100/20 to-green-200/20 rounded-full blur-3xl"></div>

        {/* Subtle nature patterns */}
        <div className="absolute inset-0 opacity-5">
          <div className="absolute top-20 left-20 w-px h-40 bg-gradient-to-b from-green-400 to-transparent"></div>
          <div className="absolute top-20 left-20 w-40 h-px bg-gradient-to-r from-green-400 to-transparent"></div>
          <div className="absolute top-60 right-40 w-px h-32 bg-gradient-to-b from-green-500 to-transparent"></div>
          <div className="absolute top-60 right-72 w-32 h-px bg-gradient-to-r from-green-500 to-transparent"></div>
          <div className="absolute bottom-40 left-1/3 w-px h-24 bg-gradient-to-b from-green-600 to-transparent"></div>
          <div className="absolute bottom-40 left-1/4 w-24 h-px bg-gradient-to-r from-green-600 to-transparent"></div>
        </div>
      </div>


      {/* <div className="relative z-10 bg-gradient-to-r from-white/90 to-green-50/90 backdrop-blur-xl border-b border-green-200/50 shadow-sm">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="text-center">
            <h1 className="text-5xl font-bold bg-gradient-to-r from-green-600 via-green-700 to-green-800 bg-clip-text text-transparent mb-3 tracking-tight">
              🌿 Sustainable Brand Analytics
            </h1>
            <p className="text-green-700 text-lg font-medium mb-4">
              Nurturing trust through transparent campaign monitoring
            </p>
            <div className="flex justify-center items-center space-x-6">
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span className="text-green-600 font-medium text-sm">Eco-Analytics Active</span>
              </div>
              <div className="w-px h-4 bg-green-300"></div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
                <span className="text-green-600 font-medium text-sm">Trust Monitoring Online</span>
              </div>
              <div className="w-px h-4 bg-green-300"></div>
              <div className="flex items-center space-x-2">
                <span className="text-green-600 font-medium text-sm">🌱 Carbon-Neutral Dashboard</span>
              </div>
            </div>
          </div>
        </div>
      </div> */}


      {/* <div className="relative z-10 max-w-7xl mx-auto px-6 py-8">

        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-green-100 hover:shadow-xl transition-all duration-300">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-600 text-sm font-medium">Total Brands</p>
                <p className="text-3xl font-bold text-green-800">{brands.length}</p>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <span className="text-2xl">🏢</span>
              </div>
            </div>
          </div>

          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-green-100 hover:shadow-xl transition-all duration-300">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-600 text-sm font-medium">Active Alerts</p>
                <p className="text-3xl font-bold text-green-800">{alerts.length}</p>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <span className="text-2xl">🚨</span>
              </div>
            </div>
          </div>

          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-green-100 hover:shadow-xl transition-all duration-300">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-600 text-sm font-medium">Avg Trust Score</p>
                <p className="text-3xl font-bold text-green-800">
                  {brands.length > 0 ? (brands.reduce((sum, b) => sum + b.avg_trust_score, 0) / brands.length).toFixed(1) : '0.0'}
                </p>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <span className="text-2xl">💚</span>
              </div>
            </div>
          </div>

          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg border border-green-100 hover:shadow-xl transition-all duration-300">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-600 text-sm font-medium">Total Mentions</p>
                <p className="text-3xl font-bold text-green-800">
                  {brands.reduce((sum, b) => sum + (b.mention_count || 0), 0).toLocaleString()}
                </p>
              </div>
              <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                <span className="text-2xl">📊</span>
              </div>
            </div>
          </div>
        </div> */}

        {/* Charts Grid */}
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">

          {/* Trust Score Bar Chart */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg border border-green-100 overflow-hidden hover:shadow-xl transition-all duration-300">
            <div className="bg-gradient-to-r from-green-500 to-green-600 px-6 py-4">
              <h2 className="text-xl font-bold text-white flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3 animate-pulse"></span>
                🌱 Brand Trust Performance
              </h2>
              <p className="text-green-100 text-sm mt-1">Sustainable brand reliability metrics</p>
            </div>
            <div className="p-6">
              <ResponsiveContainer width="100%" height={320}>
                <BarChart data={trustData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    <linearGradient id="trustGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="0%" stopColor="#4CAF50" stopOpacity={0.9}/>
                      <stop offset="50%" stopColor="#66BB6A" stopOpacity={0.8}/>
                      <stop offset="100%" stopColor="#81C784" stopOpacity={0.7}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e8f5e8" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                  />
                  <YAxis 
                    domain={[0, 100]} 
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar
                    dataKey="avg_trust_score"
                    fill="url(#trustGradient)"
                    radius={[8, 8, 0, 0]}
                    animationDuration={1000}
                  />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Scatter Chart */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg border border-green-100 overflow-hidden hover:shadow-xl transition-all duration-300">
            <div className="bg-gradient-to-r from-green-600 to-green-700 px-6 py-4">
              <h2 className="text-xl font-bold text-white flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3 animate-pulse"></span>
                📈 Brand Visibility Matrix
              </h2>
              <p className="text-green-100 text-sm mt-1">Market presence vs consumer trust analysis</p>
            </div>
            <div className="p-6">
              <ResponsiveContainer width="100%" height={320}>
                <ScatterChart margin={{ top: 20, right: 20, bottom: 40, left: 20 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e8f5e8" />
                  <XAxis
                    type="number"
                    dataKey="mention_count"
                    name="Mentions"
                    tick={{ fill: '#4a5568', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                    label={{ value: 'Mention Count', position: 'bottom', fill: '#4a5568', fontWeight: '600' }}
                    domain={['dataMin - 100', 'dataMax + 100']}
                  />
                  <YAxis
                    type="number"
                    dataKey="avg_trust_score"
                    name="Trust Score"
                    tick={{ fill: '#4a5568', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                    label={{ value: 'Trust Score', angle: -90, position: 'insideLeft', fill: '#4a5568', fontWeight: '600' }}
                    domain={[0, 100]}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Scatter
                    name="Brands"
                    data={scatterData}
                    fill="#4CAF50"
                  >
                    {scatterData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={ECO_COLORS[index % ECO_COLORS.length]} />
                    ))}
                  </Scatter>
                </ScatterChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Alerts Distribution */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg border border-green-100 overflow-hidden hover:shadow-xl transition-all duration-300">
            <div className="bg-gradient-to-r from-green-700 to-green-800 px-6 py-4">
              <h2 className="text-xl font-bold text-white flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3 animate-pulse"></span>
                🚨 Alert Distribution
              </h2>
              <p className="text-green-100 text-sm mt-1">Brand monitoring and alert severity breakdown</p>
            </div>
            <div className="p-6">
              <ResponsiveContainer width="100%" height={320}>
                <BarChart data={stackedBarData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e8f5e8" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                  />
                  <YAxis 
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '600', color: '#4a5568' }} 
                  />
                  {alertLevels.map((level, idx) => (
                    <Bar
                      key={level}
                      dataKey={level}
                      stackId="a"
                      fill={ECO_COLORS[idx % ECO_COLORS.length]}
                      radius={idx === alertLevels.length - 1 ? [8, 8, 0, 0] : 0}
                      animationDuration={1000}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Trust Score Trend */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg border border-green-100 overflow-hidden hover:shadow-xl transition-all duration-300">
            <div className="bg-gradient-to-r from-green-800 to-green-900 px-6 py-4">
              <h2 className="text-xl font-bold text-white flex items-center">
                <span className="w-2 h-2 bg-white rounded-full mr-3 animate-pulse"></span>
                📊 Trust Evolution Timeline
              </h2>
              <p className="text-green-100 text-sm mt-1">Brand trust performance over time</p>
            </div>
            <div className="p-6">
              <ResponsiveContainer width="100%" height={320}>
                <LineChart data={lineChartData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    {uniqueBrands.map((brand, idx) => (
                      <linearGradient key={`gradient-${brand}`} id={`lineGradient-${brand}`} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={ECO_COLORS[idx % ECO_COLORS.length]} stopOpacity={0.8}/>
                        <stop offset="100%" stopColor={ECO_COLORS[idx % ECO_COLORS.length]} stopOpacity={0.2}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e8f5e8" />
                  <XAxis
                    dataKey="date"
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                    tickFormatter={date => dayjs(date).format('MMM D')}
                  />
                  <YAxis
                    domain={[0, 100]}
                    tick={{ fill: '#4a5568', fontWeight: '500', fontSize: 11 }}
                    axisLine={{ stroke: '#81C784' }}
                    tickLine={{ stroke: '#81C784' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '600', color: '#4a5568' }} 
                  />
                  {uniqueBrands.map((brand, idx) => (
                    <Line
                      key={brand}
                      type="monotone"
                      dataKey={brand}
                      stroke={ECO_COLORS[idx % ECO_COLORS.length]}
                      dot={{ fill: ECO_COLORS[idx % ECO_COLORS.length], strokeWidth: 2, r: 4 }}
                      activeDot={{ r: 6, stroke: ECO_COLORS[idx % ECO_COLORS.length], strokeWidth: 2, fill: '#ffffff' }}
                      connectNulls
                      strokeWidth={3}
                      animationDuration={1000}
                    />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>

        </div>

        {/* Footer */}
        <div className="mt-12 text-center py-6 border-t border-green-200">
          <p className="text-green-600 text-sm">
            🌿 Powered by sustainable analytics • Built with care for transparency and trust
          </p>
          <div className="flex justify-center items-center space-x-4 mt-2">
            <span className="text-green-500 text-xs">🔒 Secure</span>
            <span className="text-green-500 text-xs">♻️ Eco-Friendly</span>
            <span className="text-green-500 text-xs">🌱 Sustainable</span>
          </div>
        </div>
      </div>

  );
