import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, Legend,
  ScatterChart, Scatter,
  PieChart, Pie, Cell,
} from 'recharts';

const COLORS = ['#60A5FA', '#F87171', '#34D399', '#FBBF24', '#A78BFA', '#F472B6'];
const ALERT_LEVEL_COLORS = {
  HIGH: '#F87171',
  MEDIUM: '#FBBF24',
  LOW: '#34D399',
};

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/brands')
      .then(res => setBrands(res.data))
      .catch(console.error);

    axios.get('http://localhost:5001/api/alerts?limit=100')
      .then(res => setAlerts(res.data))
      .catch(console.error);
  }, []);

  // Chart 1: Ranked Horizontal Bar for Trust
  const rankedBrands = [...brands].sort((a, b) => b.avg_trust_score - a.avg_trust_score);

  const AvgTrustBarChart = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">ðŸ”¹ Avg Trust Score (Ranked)</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart layout="vertical" data={rankedBrands}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" domain={[0, 100]} />
          <YAxis type="category" dataKey="brand" width={120} />
          <Tooltip />
          <Bar dataKey="avg_trust_score" fill="#60A5FA" radius={[0, 6, 6, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );

  // Chart 2: Mentions vs Trust Scatter
  const TrustMentionsScatter = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">ðŸ”¹ Mentions vs Trust Score</h2>
      <ResponsiveContainer width="100%" height={300}>
        <ScatterChart>
          <CartesianGrid />
          <XAxis dataKey="mention_count" name="Mentions" />
          <YAxis dataKey="avg_trust_score" name="Trust Score" domain={[0, 100]} />
          <Tooltip cursor={{ strokeDasharray: '3 3' }} />
          <Scatter data={brands} fill="#A78BFA" name="Brands" />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );

  // Chart 3: Brand-wise Pie Charts (Bias Distribution)
  const brandBiasDataMap = {};
  alerts.forEach(({ brand, bias_type }) => {
    brandBiasDataMap[brand] = brandBiasDataMap[brand] || {};
    brandBiasDataMap[brand][bias_type] = (brandBiasDataMap[brand][bias_type] || 0) + 1;
  });

  const BrandWisePieCharts = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-4">ðŸ”¹ Bias Distribution per Brand</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(brandBiasDataMap).map(([brand, biasMap]) => {
          const pieData = Object.entries(biasMap).map(([type, count]) => ({
            name: type,
            value: count,
          }));
          return (
            <div key={brand} className="text-center">
              <h3 className="font-medium mb-1">{brand}</h3>
              <ResponsiveContainer width="100%" height={200}>
                <PieChart>
                  <Pie
                    data={pieData}
                    dataKey="value"
                    nameKey="name"
                    outerRadius={60}
                    labelLine={false}
                    label={({ name, percent }) => `${name} (${(percent * 100).toFixed(0)}%)`}
                  >
                    {pieData.map((_, idx) => (
                      <Cell key={idx} fill={COLORS[idx % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </div>
          );
        })}
      </div>
    </div>
  );

  // Chart 4: Alert Severity Breakdown per Brand (Grouped Bar Chart)
  // Prepare data structure: [{brand: 'X', HIGH: 5, MEDIUM: 2, LOW: 1}, ...]
  const alertSeverityMap = {};
  alerts.forEach(({ brand, alert_level }) => {
    if (!alertSeverityMap[brand]) alertSeverityMap[brand] = { brand, HIGH: 0, MEDIUM: 0, LOW: 0 };
    if (alert_level in alertSeverityMap[brand]) alertSeverityMap[brand][alert_level]++;
  });
  const alertSeverityData = Object.values(alertSeverityMap);

  const AlertSeverityChart = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">ðŸ”¹ Alert Severity Breakdown per Brand</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={alertSeverityData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="brand" />
          <YAxis allowDecimals={false} />
          <Tooltip />
          <Legend />
          {['HIGH', 'MEDIUM', 'LOW'].map(level => (
            <Bar
              key={level}
              dataKey={level}
              stackId="a"
              fill={ALERT_LEVEL_COLORS[level]}
              name={level}
            />
          ))}
        </BarChart>
      </ResponsiveContainer>
    </div>
  );

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6 bg-gray-50 min-h-screen">
      <AvgTrustBarChart />
      <TrustMentionsScatter />
      <BrandWisePieCharts />
      <AlertSeverityChart />
    </div>
  );
}
