import React, { useEffect, useState } from 'react';
import axios from 'axios';
import dayjs from 'dayjs';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  ScatterChart,
  Scatter,
  LineChart,
  Line,
  Cell,
} from 'recharts';

const EV_COLORS = [
  '#00ff88', // Electric Green
  '#00d4ff', // Electric Blue
  '#7c3aed', // Electric Purple
  '#06b6d4', // Cyan
  '#10b981', // Emerald
  '#3b82f6', // Blue
  '#8b5cf6', // Violet
  '#0ea5e9', // Sky Blue
  '#14b8a6', // Teal
  '#6366f1', // Indigo
  '#84cc16', // Lime
  '#22c55e', // Green
];

const EV_GRADIENT_COLORS = [
  { start: '#00ff88', end: '#10b981' },
  { start: '#00d4ff', end: '#3b82f6' },
  { start: '#7c3aed', end: '#8b5cf6' },
  { start: '#06b6d4', end: '#0ea5e9' },
  { start: '#10b981', end: '#22c55e' },
  { start: '#3b82f6', end: '#6366f1' },
];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/brands').then(res => setBrands(res.data)).catch(console.error);
    axios.get('http://localhost:5001/api/alerts?limit=500').then(res => setAlerts(res.data)).catch(console.error);
  }, []);

  // Enhanced EV-themed tooltip component
  const CustomTooltip = ({ active, payload, label, backgroundColor = '#0f172a' }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-gradient-to-br from-slate-900/95 to-gray-900/95 p-4 rounded-2xl shadow-2xl border border-cyan-400/30 backdrop-blur-sm">
          <p className="font-bold text-cyan-300 mb-3 text-lg">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-sm font-medium mb-1" style={{ color: entry.color }}>
              <span className="inline-block w-3 h-3 rounded-full mr-2" style={{ backgroundColor: entry.color }}></span>
              {`${entry.name}: ${typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  // 1. Avg Trust Score by Brand
  const trustData = brands.map(({ brand, avg_trust_score }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
  }));

  // 2. Scatter Chart: brand mentions vs avg trust score
  const scatterData = brands.map(({ brand, avg_trust_score, mention_count }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
    mention_count,
  }));

  // 3. Stacked Bar Chart: Alerts by Brand & Level
  const alertLevels = Array.from(new Set(alerts.map(a => a.alert_level)));
  const brandAlertLevelMap = {};
  brands.forEach(({ brand }) => {
    brandAlertLevelMap[brand] = {};
  });
  alerts.forEach(({ brand, alert_level }) => {
    if (brandAlertLevelMap[brand]) {
      brandAlertLevelMap[brand][alert_level] = (brandAlertLevelMap[brand][alert_level] || 0) + 1;
    }
  });
  const stackedBarData = brands.map(({ brand }) => {
    const item = { brand };
    alertLevels.forEach(level => {
      item[level] = brandAlertLevelMap[brand][level] || 0;
    });
    return item;
  });

  // 4. Trust Score Trend Line Chart
  const brandDateMap = {};
  alerts.forEach(({ brand, trust_score, timestamp }) => {
    if (!brand || trust_score == null || !timestamp) return;
    const date = dayjs(timestamp).format('YYYY-MM-DD');
    const key = `${brand}-${date}`;
    if (!brandDateMap[key]) brandDateMap[key] = { brand, date, trustScores: [] };
    brandDateMap[key].trustScores.push(trust_score);
  });
  const trendData = Object.values(brandDateMap).map(({ brand, date, trustScores }) => ({
    brand,
    date,
    avgTrustScore: trustScores.reduce((a, b) => a + b, 0) / trustScores.length,
  }));
  const uniqueDates = Array.from(new Set(trendData.map(d => d.date))).sort();
  const uniqueBrands = Array.from(new Set(trendData.map(d => d.brand)));
  const lineChartData = uniqueDates.map(date => {
    const entry = { date };
    uniqueBrands.forEach(brand => {
      const found = trendData.find(d => d.brand === brand && d.date === date);
      entry[brand] = found ? Number(found.avgTrustScore.toFixed(2)) : null;
    });
    return entry;
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-slate-900 to-black relative overflow-hidden">
      {/* Animated Electric Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-cyan-500/20 to-blue-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-br from-emerald-500/20 to-green-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-gradient-to-br from-purple-500/10 to-indigo-500/10 rounded-full blur-3xl animate-pulse"></div>

        {/* Electric circuit pattern */}
        <div className="absolute inset-0 opacity-5">
          <div className="absolute top-20 left-20 w-px h-40 bg-gradient-to-b from-cyan-400 to-transparent"></div>
          <div className="absolute top-20 left-20 w-40 h-px bg-gradient-to-r from-cyan-400 to-transparent"></div>
          <div className="absolute top-60 right-40 w-px h-32 bg-gradient-to-b from-green-400 to-transparent"></div>
          <div className="absolute top-60 right-72 w-32 h-px bg-gradient-to-r from-green-400 to-transparent"></div>
          <div className="absolute bottom-40 left-1/3 w-px h-24 bg-gradient-to-b from-purple-400 to-transparent"></div>
          <div className="absolute bottom-40 left-1/4 w-24 h-px bg-gradient-to-r from-purple-400 to-transparent"></div>
        </div>
      </div>

      {/* Header */}
      <div className="relative z-10 bg-gradient-to-r from-slate-800/80 to-gray-800/80 backdrop-blur-xl border-b border-cyan-500/30">
        <div className="max-w-7xl mx-auto px-6 py-12">
          <div className="text-center">
            <h1 className="text-6xl font-black bg-gradient-to-r from-cyan-400 via-green-400 to-blue-400 bg-clip-text text-transparent mb-4 tracking-tight">
               EV Brand Analytics Hub
            </h1>
            <p className="text-cyan-100 text-xl font-medium">
               Powering insights for electric vehicle brands and performance metrics 
            </p>
            <div className="mt-6 flex justify-center items-center space-x-4">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-green-400 rounded-full animate-pulse"></div>
                <span className="text-green-400 font-medium">System Online</span>
              </div>
              <div className="w-px h-4 bg-gray-600"></div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-cyan-400 rounded-full animate-pulse"></div>
                <span className="text-cyan-400 font-medium">Data Stream Active</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-6 py-12">
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">

          {/* Avg Trust Score Bar Chart */}
          <div className="bg-gradient-to-br from-slate-800/40 to-gray-800/40 backdrop-blur-xl rounded-3xl shadow-2xl border border-cyan-500/20 overflow-hidden hover:shadow-cyan-500/25 transition-all duration-300 hover:border-cyan-400/40">
            <div className="bg-gradient-to-r from-cyan-600 via-blue-600 to-indigo-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                 Brand Trust Performance
              </h2>
              <p className="text-cyan-100 text-sm mt-2 font-medium">Electric vehicle brand reliability index</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <BarChart data={trustData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    <linearGradient id="trustGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="0%" stopColor="#00ff88" stopOpacity={0.9}/>
                      <stop offset="50%" stopColor="#00d4ff" stopOpacity={0.8}/>
                      <stop offset="100%" stopColor="#7c3aed" stopOpacity={0.7}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                  />
                  <YAxis 
                    domain={[0, 100]} 
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar
                    dataKey="avg_trust_score"
                    fill="url(#trustGradient)"
                    radius={[12, 12, 0, 0]}
                    animationDuration={1000}
                  />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Scatter Chart: Mentions vs Trust Score */}
          <div className="bg-gradient-to-br from-slate-800/40 to-gray-800/40 backdrop-blur-xl rounded-3xl shadow-2xl border border-emerald-500/20 overflow-hidden hover:shadow-emerald-500/25 transition-all duration-300 hover:border-emerald-400/40">
            <div className="bg-gradient-to-r from-emerald-600 via-green-600 to-teal-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                 Brand Visibility Matrix
              </h2>
              <p className="text-emerald-100 text-sm mt-2 font-medium">Market presence vs consumer trust correlation</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <ScatterChart margin={{ top: 20, right: 20, bottom: 40, left: 20 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis
                    type="number"
                    dataKey="mention_count"
                    name="Mentions"
                    tick={{ fill: '#94a3b8', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                    label={{ value: 'Mention Count', position: 'bottom', fill: '#94a3b8', fontWeight: '700' }}
                    domain={['dataMin - 100', 'dataMax + 100']}
                  />
                  <YAxis
                    type="number"
                    dataKey="avg_trust_score"
                    name="Trust Score"
                    tick={{ fill: '#94a3b8', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                    label={{ value: 'Avg Trust Score', angle: -90, position: 'insideLeft', fill: '#94a3b8', fontWeight: '700' }}
                    domain={[0, 100]}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Scatter
                    name="Brands"
                    data={scatterData}
                    fill="#00ff88"
                  >
                    {scatterData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={EV_COLORS[index % EV_COLORS.length]} />
                    ))}
                  </Scatter>
                </ScatterChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Stacked Bar Chart: Alerts by Brand and Level */}
          <div className="bg-gradient-to-br from-slate-800/40 to-gray-800/40 backdrop-blur-xl rounded-3xl shadow-2xl border border-purple-500/20 overflow-hidden hover:shadow-purple-500/25 transition-all duration-300 hover:border-purple-400/40">
            <div className="bg-gradient-to-r from-purple-600 via-violet-600 to-indigo-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                 Alert Distribution System
              </h2>
              <p className="text-purple-100 text-sm mt-2 font-medium">Brand monitoring and alert severity analysis</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <BarChart data={stackedBarData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                  />
                  <YAxis 
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '700', color: '#94a3b8' }} 
                  />
                  {alertLevels.map((level, idx) => (
                    <Bar
                      key={level}
                      dataKey={level}
                      stackId="a"
                      fill={EV_COLORS[idx % EV_COLORS.length]}
                      radius={idx === alertLevels.length - 1 ? [12, 12, 0, 0] : 0}
                      animationDuration={1000}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Trust Score Trend Line Chart */}
          <div className="bg-gradient-to-br from-slate-800/40 to-gray-800/40 backdrop-blur-xl rounded-3xl shadow-2xl border border-blue-500/20 overflow-hidden hover:shadow-blue-500/25 transition-all duration-300 hover:border-blue-400/40">
            <div className="bg-gradient-to-r from-blue-600 via-cyan-600 to-teal-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                 Trust Evolution Timeline
              </h2>
              <p className="text-blue-100 text-sm mt-2 font-medium">Brand trust performance over time periods</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <LineChart data={lineChartData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    {uniqueBrands.map((brand, idx) => (
                      <linearGradient key={`gradient-${brand}`} id={`lineGradient-${brand}`} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={EV_COLORS[idx % EV_COLORS.length]} stopOpacity={0.8}/>
                        <stop offset="100%" stopColor={EV_COLORS[idx % EV_COLORS.length]} stopOpacity={0.2}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis
                    dataKey="date"
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                    tickFormatter={date => dayjs(date).format('MMM D')}
                  />
                  <YAxis
                    domain={[0, 100]}
                    tick={{ fill: '#94a3b8', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#475569' }}
                    tickLine={{ stroke: '#475569' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '700', color: '#94a3b8' }} 
                  />
                  {uniqueBrands.map((brand, idx) => (
                    <Line
                      key={brand}
                      type="monotone"
                      dataKey={brand}
                      stroke={EV_COLORS[idx % EV_COLORS.length]}
                      dot={{ fill: EV_COLORS[idx % EV_COLORS.length], strokeWidth: 3, r: 6 }}
                      activeDot={{ r: 8, stroke: EV_COLORS[idx % EV_COLORS.length], strokeWidth: 3, fill: '#ffffff' }}
                      connectNulls
                      strokeWidth={4}
                      animationDuration={1000}
                    />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>

        </div>

        {/* EV-themed Stats Footer */}
        <div className="mt-16 grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="bg-gradient-to-br from-cyan-500/20 to-blue-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-cyan-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-cyan-300 mb-3">
              {brands.length}
            </div>
            <div className="text-cyan-100 font-bold text-lg"> EV Brands</div>
          </div>
          <div className="bg-gradient-to-br from-emerald-500/20 to-green-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-emerald-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-emerald-300 mb-3">
              {alerts.length}
            </div>
            <div className="text-emerald-100 font-bold text-lg"> System Alerts</div>
          </div>
          <div className="bg-gradient-to-br from-purple-500/20 to-violet-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-purple-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-purple-300 mb-3">
              {brands.length > 0 ? Math.round(brands.reduce((sum, brand) => sum + brand.avg_trust_score, 0) / brands.length) : 0}
            </div>
            <div className="text-purple-100 font-bold text-lg"> Trust Index</div>
          </div>
          <div className="bg-gradient-to-br from-blue-500/20 to-indigo-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-blue-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-blue-300 mb-3">
              {brands.length > 0 ? brands.reduce((sum, brand) => sum + brand.mention_count, 0).toLocaleString() : 0}
            </div>
            <div className="text-blue-100 font-bold text-lg"> Data Points</div>
          </div>
        </div>
      </div>
    </div>
  );
}
