// FullDashboard.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import dayjs from 'dayjs';
import {
  BarChart, Bar, LineChart, Line,
  XAxis, YAxis, Tooltip, CartesianGrid,
  ResponsiveContainer, PieChart, Pie, Cell, Legend
} from 'recharts';

// Tailwind-friendly color palette
const COLORS = ['#60A5FA', '#F87171', '#34D399', '#FBBF24', '#A78BFA', '#F472B6'];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/brands')
      .then(res => setBrands(res.data))
      .catch(console.error);

    axios.get('http://localhost:5001/api/alerts?limit=100')
      .then(res => setAlerts(res.data))
      .catch(console.error);
  }, []);

  // --- CHART 1: Avg Trust Score per Brand ---
  const AvgTrustScoreChart = () => (
    <div className="p-4 bg-white rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">Average Trust Score per Brand</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={brands}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="brand" />
          <YAxis domain={[0, 100]} />
          <Tooltip />
          <Bar dataKey="avg_trust_score" fill="#60A5FA" radius={[4, 4, 0, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );

  // --- CHART 3: Bias Type Distribution ---
  const biasTypeCounts = alerts.reduce((acc, alert) => {
    acc[alert.bias_type] = (acc[alert.bias_type] || 0) + 1;
    return acc;
  }, {});
  const biasTypeData = Object.entries(biasTypeCounts).map(([name, value]) => ({ name, value }));

  const BiasTypeDistribution = () => (
    <div className="p-4 bg-white rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">Bias Type Distribution</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie data={biasTypeData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} label>
            {biasTypeData.map((_, index) => (
              <Cell key={index} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>
    </div>
  );

  // --- CHART 4: Bias Heatmap (Brand Ã— Bias Type) ---
  const brandBiasMatrix = {};
  const brandSet = new Set();
  const biasSet = new Set();
  alerts.forEach(alert => {
    brandSet.add(alert.brand);
    biasSet.add(alert.bias_type);
    brandBiasMatrix[alert.brand] = brandBiasMatrix[alert.brand] || {};
    brandBiasMatrix[alert.brand][alert.bias_type] = (brandBiasMatrix[alert.brand][alert.bias_type] || 0) + 1;
  });

  const brandsList = [...brandSet];
  const biasTypes = [...biasSet];

  const BiasHeatmapChart = () => (
    <div className="p-4 bg-white rounded-2xl shadow-md overflow-x-auto">
      <h2 className="text-xl font-semibold mb-2">Bias Heatmap (Brand Ã— Bias Type)</h2>
      <table className="min-w-full text-sm text-center border">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-2 py-1">Brand</th>
            {biasTypes.map((bias) => (
              <th key={bias} className="border px-2 py-1">{bias}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {brandsList.map((brand) => (
            <tr key={brand}>
              <td className="border px-2 py-1 font-medium">{brand}</td>
              {biasTypes.map((bias) => {
                const count = brandBiasMatrix[brand]?.[bias] || 0;
                const intensity = Math.min(255, count * 30);
                const bgColor = `rgba(96,165,250,${count ? 0.3 + intensity / 300 : 0.05})`;
                return (
                  <td key={bias} className="border px-2 py-1" style={{ backgroundColor: bgColor }}>
                    {count}
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  // --- CHART 8: Last Mention Timeline ---
  const lastMentionData = brands.map(b => ({
    brand: b.brand,
    date: dayjs(b.last_mention).format('YYYY-MM-DD')
  }));

  const RecentMentionsChart = () => (
    <div className="p-4 bg-white rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">Last Mention Timeline</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={lastMentionData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="brand" />
          <YAxis hide />
          <Tooltip labelFormatter={(label) => `Brand: ${label}`} />
          <Line type="monotone" dataKey="date" stroke="#34D399" strokeWidth={2} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6">
      <AvgTrustScoreChart />
      <BiasTypeDistribution />
      <BiasHeatmapChart />
      <RecentMentionsChart />
    </div>
  );
}
