import React, { useEffect, useState } from 'react';
import axios from 'axios';
import dayjs from 'dayjs';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  ScatterChart,
  Scatter,
  LineChart,
  Line,
  Cell,
} from 'recharts';

const COLORS = [
  '#ff6b6b', // Vibrant Red
  '#4ecdc4', // Turquoise
  '#45b7d1', // Sky Blue
  '#96ceb4', // Mint Green
  '#feca57', // Golden Yellow
  '#ff9ff3', // Hot Pink
  '#54a0ff', // Dodger Blue
  '#5f27cd', // Purple
  '#00d2d3', // Cyan
  '#ff9f43', // Orange
  '#10ac84', // Emerald
  '#ee5a24', // Sunset Orange
];

const GRADIENT_COLORS = [
  { start: '#ff6b6b', end: '#ee5a24' },
  { start: '#4ecdc4', end: '#10ac84' },
  { start: '#45b7d1', end: '#5f27cd' },
  { start: '#96ceb4', end: '#00d2d3' },
  { start: '#feca57', end: '#ff9f43' },
  { start: '#ff9ff3', end: '#ff6b6b' },
];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/brands').then(res => setBrands(res.data)).catch(console.error);
    axios.get('http://localhost:5001/api/alerts?limit=500').then(res => setAlerts(res.data)).catch(console.error);
  }, []);

  // Enhanced colorful tooltip component
  const CustomTooltip = ({ active, payload, label, backgroundColor = '#1a1a2e' }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-gradient-to-br from-purple-900 to-blue-900 p-4 rounded-xl shadow-2xl border border-purple-500/30 backdrop-blur-sm">
          <p className="font-bold text-white mb-3 text-lg">{label}</p>
          {payload.map((entry, index) => (
            <p key={index} className="text-sm font-medium mb-1" style={{ color: entry.color }}>
              <span className="inline-block w-3 h-3 rounded-full mr-2" style={{ backgroundColor: entry.color }}></span>
              {`${entry.name}: ${typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}`}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  // 1. Avg Trust Score by Brand
  const trustData = brands.map(({ brand, avg_trust_score }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
  }));

  // 2. Scatter Chart: brand mentions vs avg trust score
  const scatterData = brands.map(({ brand, avg_trust_score, mention_count }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
    mention_count,
  }));

  // 3. Stacked Bar Chart: Alerts by Brand & Level
  const alertLevels = Array.from(new Set(alerts.map(a => a.alert_level)));
  const brandAlertLevelMap = {};
  brands.forEach(({ brand }) => {
    brandAlertLevelMap[brand] = {};
  });
  alerts.forEach(({ brand, alert_level }) => {
    if (brandAlertLevelMap[brand]) {
      brandAlertLevelMap[brand][alert_level] = (brandAlertLevelMap[brand][alert_level] || 0) + 1;
    }
  });
  const stackedBarData = brands.map(({ brand }) => {
    const item = { brand };
    alertLevels.forEach(level => {
      item[level] = brandAlertLevelMap[brand][level] || 0;
    });
    return item;
  });

  // 4. Trust Score Trend Line Chart
  const brandDateMap = {};
  alerts.forEach(({ brand, trust_score, timestamp }) => {
    if (!brand || trust_score == null || !timestamp) return;
    const date = dayjs(timestamp).format('YYYY-MM-DD');
    const key = `${brand}-${date}`;
    if (!brandDateMap[key]) brandDateMap[key] = { brand, date, trustScores: [] };
    brandDateMap[key].trustScores.push(trust_score);
  });
  const trendData = Object.values(brandDateMap).map(({ brand, date, trustScores }) => ({
    brand,
    date,
    avgTrustScore: trustScores.reduce((a, b) => a + b, 0) / trustScores.length,
  }));
  const uniqueDates = Array.from(new Set(trendData.map(d => d.date))).sort();
  const uniqueBrands = Array.from(new Set(trendData.map(d => d.brand)));
  const lineChartData = uniqueDates.map(date => {
    const entry = { date };
    uniqueBrands.forEach(brand => {
      const found = trendData.find(d => d.brand === brand && d.date === date);
      entry[brand] = found ? Number(found.avgTrustScore.toFixed(2)) : null;
    });
    return entry;
  });

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-pink-500/20 to-purple-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-br from-cyan-500/20 to-blue-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-gradient-to-br from-yellow-500/10 to-orange-500/10 rounded-full blur-3xl animate-pulse"></div>
      </div>

      {/* Header */}
      <div className="relative z-10 bg-gradient-to-r from-purple-800/80 to-blue-800/80 backdrop-blur-xl border-b border-purple-500/30">
        <div className="max-w-7xl mx-auto px-6 py-12">
          <div className="text-center">
            <h1 className="text-6xl font-black bg-gradient-to-r from-pink-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent mb-4 tracking-tight">
              Brand Analytics Dashboard
            </h1>
            <p className="text-blue-100 text-xl font-medium">
              🚀 Comprehensive insights into brand performance and trust metrics ✨
            </p>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-6 py-12">
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-8">

          {/* Avg Trust Score Bar Chart */}
          <div className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 overflow-hidden hover:shadow-purple-500/25 transition-all duration-300">
            <div className="bg-gradient-to-r from-purple-600 via-pink-600 to-red-500 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                📊 Average Trust Score by Brand
              </h2>
              <p className="text-purple-100 text-sm mt-2 font-medium">Performance ranking across all brands</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <BarChart data={trustData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    <linearGradient id="trustGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="0%" stopColor="#ff6b6b" stopOpacity={0.9}/>
                      <stop offset="50%" stopColor="#4ecdc4" stopOpacity={0.8}/>
                      <stop offset="100%" stopColor="#45b7d1" stopOpacity={0.7}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff30" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                  />
                  <YAxis 
                    domain={[0, 100]} 
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar
                    dataKey="avg_trust_score"
                    fill="url(#trustGradient)"
                    radius={[12, 12, 0, 0]}
                    animationDuration={1000}
                  />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Scatter Chart: Mentions vs Trust Score */}
          <div className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 overflow-hidden hover:shadow-cyan-500/25 transition-all duration-300">
            <div className="bg-gradient-to-r from-cyan-600 via-teal-600 to-green-500 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                🎯 Brand Mentions vs Trust Score
              </h2>
              <p className="text-cyan-100 text-sm mt-2 font-medium">Correlation between visibility and trust</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <ScatterChart margin={{ top: 20, right: 20, bottom: 40, left: 20 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff30" />
                  <XAxis
                    type="number"
                    dataKey="mention_count"
                    name="Mentions"
                    tick={{ fill: '#ffffff', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                    label={{ value: 'Mention Count', position: 'bottom', fill: '#ffffff', fontWeight: '700' }}
                    domain={['dataMin - 100', 'dataMax + 100']}
                  />
                  <YAxis
                    type="number"
                    dataKey="avg_trust_score"
                    name="Trust Score"
                    tick={{ fill: '#ffffff', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                    label={{ value: 'Avg Trust Score', angle: -90, position: 'insideLeft', fill: '#ffffff', fontWeight: '700' }}
                    domain={[0, 100]}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Scatter
                    name="Brands"
                    data={scatterData}
                    fill="#ec4899"
                  >
                    {scatterData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Scatter>
                </ScatterChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Stacked Bar Chart: Alerts by Brand and Level */}
          <div className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 overflow-hidden hover:shadow-green-500/25 transition-all duration-300">
            <div className="bg-gradient-to-r from-green-600 via-emerald-600 to-teal-500 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                ⚠️ Alerts by Brand and Severity
              </h2>
              <p className="text-green-100 text-sm mt-2 font-medium">Alert distribution across severity levels</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <BarChart data={stackedBarData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff30" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                  />
                  <YAxis 
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '700', color: '#ffffff' }} 
                  />
                  {alertLevels.map((level, idx) => (
                    <Bar
                      key={level}
                      dataKey={level}
                      stackId="a"
                      fill={COLORS[idx % COLORS.length]}
                      radius={idx === alertLevels.length - 1 ? [12, 12, 0, 0] : 0}
                      animationDuration={1000}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Trust Score Trend Line Chart */}
          <div className="bg-gradient-to-br from-white/10 to-white/5 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 overflow-hidden hover:shadow-yellow-500/25 transition-all duration-300">
            <div className="bg-gradient-to-r from-yellow-600 via-orange-600 to-red-500 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center">
                <div className="w-3 h-3 bg-white rounded-full mr-4 animate-pulse"></div>
                📈 Trust Score Trend Over Time
              </h2>
              <p className="text-yellow-100 text-sm mt-2 font-medium">Performance trends across time periods</p>
            </div>
            <div className="p-8">
              <ResponsiveContainer width="100%" height={380}>
                <LineChart data={lineChartData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    {uniqueBrands.map((brand, idx) => (
                      <linearGradient key={`gradient-${brand}`} id={`lineGradient-${brand}`} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={COLORS[idx % COLORS.length]} stopOpacity={0.8}/>
                        <stop offset="100%" stopColor={COLORS[idx % COLORS.length]} stopOpacity={0.2}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff30" />
                  <XAxis
                    dataKey="date"
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                    tickFormatter={date => dayjs(date).format('MMM D')}
                  />
                  <YAxis
                    domain={[0, 100]}
                    tick={{ fill: '#ffffff', fontWeight: '600', fontSize: 12 }}
                    axisLine={{ stroke: '#ffffff40' }}
                    tickLine={{ stroke: '#ffffff40' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend 
                    verticalAlign="top" 
                    height={36}
                    wrapperStyle={{ fontWeight: '700', color: '#ffffff' }} 
                  />
                  {uniqueBrands.map((brand, idx) => (
                    <Line
                      key={brand}
                      type="monotone"
                      dataKey={brand}
                      stroke={COLORS[idx % COLORS.length]}
                      dot={{ fill: COLORS[idx % COLORS.length], strokeWidth: 3, r: 6 }}
                      activeDot={{ r: 8, stroke: COLORS[idx % COLORS.length], strokeWidth: 3, fill: '#ffffff' }}
                      connectNulls
                      strokeWidth={4}
                      animationDuration={1000}
                    />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>

        </div>

        {/* Colorful Stats Footer */}
        <div className="mt-16 grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="bg-gradient-to-br from-purple-500/20 to-pink-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-purple-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-purple-300 mb-3">
              {brands.length}
            </div>
            <div className="text-white font-bold text-lg">🏢 Total Brands</div>
          </div>
          <div className="bg-gradient-to-br from-cyan-500/20 to-blue-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-cyan-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-cyan-300 mb-3">
              {alerts.length}
            </div>
            <div className="text-white font-bold text-lg">🚨 Total Alerts</div>
          </div>
          <div className="bg-gradient-to-br from-green-500/20 to-emerald-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-green-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-green-300 mb-3">
              {brands.length > 0 ? Math.round(brands.reduce((sum, brand) => sum + brand.avg_trust_score, 0) / brands.length) : 0}
            </div>
            <div className="text-white font-bold text-lg">📊 Avg Trust Score</div>
          </div>
          <div className="bg-gradient-to-br from-yellow-500/20 to-orange-500/20 backdrop-blur-xl rounded-2xl p-6 text-center border border-yellow-500/30 hover:scale-105 transition-transform duration-300">
            <div className="text-4xl font-black text-yellow-300 mb-3">
              {brands.length > 0 ? brands.reduce((sum, brand) => sum + brand.mention_count, 0).toLocaleString() : 0}
            </div>
            <div className="text-white font-bold text-lg">💬 Total Mentions</div>
          </div>
        </div>
      </div>
    </div>
  );
}
