import React from 'react'

interface Props {
  render?: (x: number, y: number, ratio: number) => {}
  posX: number
  posY: number
  style?: (x: number, y: number, ratio: number) => {}
  ratio: number
  value: number
  height?: string
  square?: boolean
  onClick?: (x: number, y: number) => void
}

const noop = (returnVal: any) => () => returnVal

const Cell = ({
  render = noop(null),
  style = noop({}),
  ratio,
  posX,
  posY,
  square = false,
  height = '2rem',
  value,
  onClick
}: Props) => {
  return (
    <div
      onClick={() => (onClick || noop({}))(posX, posY)}
      style={{
        border: '1px solid #fff',
        borderWidth: '1px 1px 0 0',
        textAlign: 'center',
        color: `rgb(0, 0, 0, ${ratio / 2 + 0.4})`,
        overflow: 'hidden',
        boxSizing: 'border-box',
        flexGrow: square ? 0 : 1,
        flexBasis: square ? height : 0,
        flexShrink: 0,
        height: height,
        lineHeight: height,
        borderRadius: '4px',
        fontSize: '.8rem',
        cursor: onClick ? 'pointer' : 'initial',
        background: `rgb(12, 160, 44, ${ratio + 0.05})`,
        ...style(posX, posY, ratio)
      }}
    >
      {render(posX, posY, value)}
    </div>
  )
}

export default React.memo(Cell)
import React from 'react'

interface Props {
  children: any
  reverse?: boolean
}

export default function Row({ children, reverse = false }: Props) {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: reverse ? 'row-reverse' : 'row',
        justifyContent: reverse ? 'flex-end' : 'initial'
      }}
    >
      {children}
    </div>
  )
}
import React from 'react'

interface Props {
  labels: string[]
  xLabelsStyle?: (index: number) => {}
  square?: boolean
  height: string
}

export default function XLabels({
  labels,
  xLabelsStyle = () => ({}),
  height,
  square = false
}: Props) {
  const widthPercent = `${100 / labels.length}%`
  return (
    <div style={{ display: 'flex', textAlign: 'center' }}>
      {labels.map((label, index) => (
        <div
          key={`${label}-${index}`}
          style={{
            padding: '0.2rem 0',
            boxSizing: 'border-box',
            flexGrow: square ? 'initial' : 1,
            overflow: 'hidden',
            flexShrink: 1,
            flexBasis: square ? height : widthPercent,
            width: square ? height : widthPercent,
            ...xLabelsStyle(index)
          }}
        >
          {label}
        </div>
      ))}
    </div>
  )
}
import React from 'react'

interface Props {
  children: any
  reverse?: boolean
  grow?: boolean
}

export default function Column({
  children,
  grow = false,
  reverse = false
}: Props) {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: reverse ? 'column-reverse' : 'column',
        flexGrow: grow ? 1 : 0
      }}
    >
      {children}
    </div>
  )
}
import React from 'react'

interface Props {
  labels: string[]
  height: string
  reverse?: boolean
  yLabelsStyle?: (index: number) => {}
}

export default function YLabels({
  labels,
  height,
  yLabelsStyle = () => ({}),
  reverse = false
}: Props) {
  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        textAlign: reverse ? 'left' : 'right'
      }}
    >
      {labels.map((label, index) => (
        <div
          key={label}
          style={{
            boxSizing: 'border-box',
            padding: '0 0.2rem',
            lineHeight: height,
            ...yLabelsStyle(index)
          }}
        >
          {label}
        </div>
      ))}
    </div>
  )
}
import React from 'react'

interface Props {
  xLabelHeight: number
  isXLabelReverse: boolean
  children: any
}

export default function YLabelAligner({
  xLabelHeight,
  isXLabelReverse,
  children
}: Props) {
  const style = {
    [isXLabelReverse ? 'marginBottom' : 'marginTop']: `${xLabelHeight}px`
  }
  return <div style={style}>{children}</div>
}
import React from 'react'

interface ClientHeight {
  clientHeight: number
}

export default function useElementHeight(
  initHeight: number
): [number, React.MutableRefObject<any>] {
  const eleRef = React.useRef(null)
  const [eleHeight, setEleHeight] = React.useState<number>(initHeight)

  React.useEffect(() => {
    if (eleRef.current) {
      const height = ((eleRef.current || {}) as ClientHeight).clientHeight
      setEleHeight(height)
    }
  }, [])

  return [eleHeight, eleRef]
}
import * as React from 'react'
import Cell from './Cell'
import Row from './Row'
import XLabels from './XLabels'
import Column from './Column'
import YLabels from './YLabels'
import YLabelAligner from './YLabelAligner'
import useElementHeight from './useElementHeight'

export interface HeatMapGridProps {
  data: number[][]
  xLabels?: string[]
  yLabels?: string[]
  cellHeight?: string
  square?: boolean
  xLabelsPos?: 'top' | 'bottom'
  yLabelsPos?: 'left' | 'right'
  xLabelsStyle?: (index: number) => {}
  yLabelsStyle?: (index: number) => {}
  cellStyle?: (x: number, y: number, ratio: number) => {}
  cellRender?: (x: number, y: number, value: number) => {}
  onClick?: (x: number, y: number) => void
}

function getMinMax(data: number[][]): [number, number] {
  const flatArray = data.reduce((i, o) => [...o, ...i], [])
  const max = Math.max(...flatArray)
  const min = Math.min(...flatArray)
  return [min, max]
}

export const HeatMapGrid = ({
  data,
  xLabels,
  yLabels,
  xLabelsPos = 'top',
  yLabelsPos = 'left',
  square = false,
  cellHeight = '2px',
  xLabelsStyle,
  yLabelsStyle,
  cellStyle,
  cellRender,
  onClick
}: HeatMapGridProps) => {
  const [xLabelHeight, xLabelRef] = useElementHeight(22)
  const [min, max] = getMinMax(data)
  const minMaxDiff = max - min
  const isXLabelReverse = xLabelsPos === 'bottom'
  const isYLabelReverse = yLabelsPos === 'right'

  return (
    <Row reverse={isYLabelReverse}>
      {yLabels && (
        <YLabelAligner
          xLabelHeight={xLabelHeight}
          isXLabelReverse={isXLabelReverse}
        >
          <YLabels
            reverse={isYLabelReverse}
            labels={yLabels}
            height={cellHeight}
            yLabelsStyle={yLabelsStyle}
          />
        </YLabelAligner>
      )}
      <Column reverse={isXLabelReverse} grow={!square}>
        <div ref={xLabelRef}>
          {xLabels && (
            <XLabels
              labels={xLabels}
              xLabelsStyle={xLabelsStyle}
              height={cellHeight}
              square={square}
            />
          )}
        </div>
        <Column>
          {data.map((rowItems, xi) => (
            <Row key={xi}>
              {rowItems.map((value, yi) => (
                <Cell
                  key={`${xi}-${yi}`}
                  posX={xi}
                  posY={yi}
                  onClick={onClick}
                  value={value}
                  height={cellHeight}
                  square={square}
                  render={cellRender}
                  style={cellStyle}
                  ratio={(value - min) / minMaxDiff}
                />
              ))}
            </Row>
          ))}
        </Column>
      </Column>
    </Row>
  )
}
