// BiasHeatmapChart.jsx
import React from 'react';
import { HeatMapGrid } from 'react-grid-heatmap';

const BiasHeatmapChart = ({ data = {}, title = 'Live Ethical Bias Heatmap' }) => {
  const matrix = data?.matrix || [];
  const xLabels = data?.xLabels || [];
  const yLabels = data?.yLabels || [];

  return (
    <div className="p-4 bg-white rounded-2xl shadow overflow-x-auto">
      <h2 className="text-xl font-semibold mb-2">{title}</h2>
      <HeatMapGrid
        data={matrix}
        xLabels={xLabels}
        yLabels={yLabels}
        cellStyle={(_x, _y, value) => ({
          background: `rgb(255, ${255 - value * 2}, ${255 - value * 2})`,
          fontSize: '12px',
        })}
        cellRender={value => value}
      />
    </div>
  );
};

export default BiasHeatmapChart;
