// BiasHeatmapChart.jsx
import React from 'react';
import HeatMap from '@uiw/react-heat-map';

const BiasHeatmapChart = ({ data = {}, title = 'Live Ethical Bias Heatmap' }) => {
  const values = (data?.matrix || []).flatMap((row, y) =>
    row.map((val, x) => ({
      date: `2025-07-${(x + y) % 30 + 1}`, // dummy date format
      count: val,
    }))
  );

  return (
    <div className="p-4 bg-white rounded-2xl shadow overflow-x-auto">
      <h2 className="text-xl font-semibold mb-2">{title}</h2>
      <HeatMap
        value={values}
        weekLabels={['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat']}
        startDate={new Date('2025/07/01')}
        panelColors={{
          0: '#e0e0e0',
          5: '#a0d0ff',
          10: '#3090ff',
          20: '#004080',
        }}
        rectSize={12}
        legendCellSize={12}
        space={2}
      />
    </div>
  );
};

export default BiasHeatmapChart;
