import React, { useEffect, useState } from 'react';
import {
  format,
  parseISO,
  startOfDay,
  endOfDay,
  isValid
} from 'date-fns';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  Legend,
  ScatterChart,
  Scatter,
  LineChart,
  Line,
  Cell,
  PieChart,
  Pie,
  RadialBarChart,
  RadialBar,
  Area,
  AreaChart,
} from 'recharts';

const COLORS = [
  '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57', '#FF9FF3',
  '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43', '#10AC84', '#EE5A24',
  '#C44569', '#F8B500', '#6C5CE7', '#A29BFE', '#FD79A8', '#E17055',
  '#00B894', '#0984E3', '#E84393', '#FDCB6E', '#6C5CE7', '#74B9FF'
];

const NEON_COLORS = [
  '#FF1744', '#FF6D00', '#FFEA00', '#76FF03', '#00E676', '#00E5FF',
  '#3D5AFE', '#651FFF', '#E91E63', '#FF5722', '#795548', '#607D8B'
];

const GRADIENT_DEFS = [
  { id: 'neonPink', start: '#FF1744', end: '#E91E63' },
  { id: 'neonBlue', start: '#00E5FF', end: '#3D5AFE' },
  { id: 'neonGreen', start: '#76FF03', end: '#00E676' },
  { id: 'neonPurple', start: '#651FFF', end: '#E91E63' },
  { id: 'neonOrange', start: '#FF6D00', end: '#FF5722' },
  { id: 'neonCyan', start: '#00E5FF', end: '#00E676' },
];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5001/api/brands')
      .then(res => res.json())
      .then(data => setBrands(data))
      .catch(console.error);

    fetch('http://localhost:5001/api/alerts?limit=500')
      .then(res => res.json())
      .then(data => setAlerts(data))
      .catch(console.error);
  }, []);

  // Enhanced neon tooltip
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-black/90 backdrop-blur-xl p-6 rounded-2xl shadow-2xl border-2 border-cyan-400/50 relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-purple-500/20 to-cyan-500/20 animate-pulse"></div>
          <div className="relative z-10">
            <p className="font-bold text-white mb-3 text-lg glow-text">{label}</p>
            {payload.map((entry, index) => (
              <div key={index} className="flex items-center mb-2">
                <div 
                  className="w-4 h-4 rounded-full mr-3 shadow-lg"
                  style={{ 
                    backgroundColor: entry.color,
                    boxShadow: `0 0 10px ${entry.color}50`
                  }}
                ></div>
                <span className="text-white font-medium">
                  {entry.name}: <span className="text-cyan-300 font-bold">{typeof entry.value === 'number' ? entry.value.toFixed(2) : entry.value}</span>
                </span>
              </div>
            ))}
          </div>
        </div>
      );
    }
    return null;
  };

  // Data processing (same as original)
  const trustData = brands.map(({ brand, avg_trust_score }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
  }));

  const scatterData = brands.map(({ brand, avg_trust_score, mention_count }) => ({
    brand,
    avg_trust_score: Number(avg_trust_score.toFixed(2)),
    mention_count,
  }));

  const alertLevels = Array.from(new Set(alerts.map(a => a.alert_level)));
  const brandAlertLevelMap = {};
  brands.forEach(({ brand }) => {
    brandAlertLevelMap[brand] = {};
  });
  alerts.forEach(({ brand, alert_level }) => {
    if (brandAlertLevelMap[brand]) {
      brandAlertLevelMap[brand][alert_level] = (brandAlertLevelMap[brand][alert_level] || 0) + 1;
    }
  });
  const stackedBarData = brands.map(({ brand }) => {
    const item = { brand };
    alertLevels.forEach(level => {
      item[level] = brandAlertLevelMap[brand][level] || 0;
    });
    return item;
  });

  const brandDateMap = {};
  alerts.forEach(({ brand, trust_score, timestamp }) => {
    if (!brand || trust_score == null || !timestamp) return;

    let date;
    try {
      const parsedDate = parseISO(timestamp);
      date = isValid(parsedDate) ? format(parsedDate, 'yyyy-MM-dd') : new Date(timestamp).toISOString().split('T')[0];
    } catch (e) {
      date = new Date(timestamp).toISOString().split('T')[0];
    }

    const key = `${brand}-${date}`;
    if (!brandDateMap[key]) brandDateMap[key] = { brand, date, trustScores: [] };
    brandDateMap[key].trustScores.push(trust_score);
  });

  const trendData = Object.values(brandDateMap).map(({ brand, date, trustScores }) => ({
    brand,
    date,
    avgTrustScore: trustScores.reduce((a, b) => a + b, 0) / trustScores.length,
  }));

  const uniqueDates = Array.from(new Set(trendData.map(d => d.date))).sort();
  const uniqueBrands = Array.from(new Set(trendData.map(d => d.brand)));

  const lineChartData = uniqueDates.map(date => {
    const entry = { date };
    uniqueBrands.forEach(brand => {
      const found = trendData.find(d => d.brand === brand && d.date === date);
      entry[brand] = found ? Number(found.avgTrustScore.toFixed(2)) : null;
    });
    return entry;
  });

  // Pie chart data for alert levels
  const alertLevelCounts = alertLevels.map(level => ({
    name: level,
    value: alerts.filter(alert => alert.alert_level === level).length
  }));

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-black relative overflow-hidden">
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }
        @keyframes glow {
          0%, 100% { box-shadow: 0 0 20px rgba(255, 255, 255, 0.1); }
          50% { box-shadow: 0 0 40px rgba(255, 255, 255, 0.3), 0 0 60px rgba(255, 255, 255, 0.2); }
        }
        @keyframes neon {
          0%, 100% { text-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #00ffff, 0 0 20px #00ffff; }
          50% { text-shadow: 0 0 10px #fff, 0 0 20px #fff, 0 0 30px #00ffff, 0 0 40px #00ffff; }
        }
        .float-animation { animation: float 6s ease-in-out infinite; }
        .glow-box { animation: glow 3s ease-in-out infinite alternate; }
        .glow-text { animation: neon 2s ease-in-out infinite alternate; }
        .chart-container {
          background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%);
          backdrop-filter: blur(20px);
          border: 1px solid rgba(255,255,255,0.2);
          box-shadow: 0 20px 40px rgba(0,0,0,0.3);
        }
        .neon-border {
          border: 2px solid transparent;
          background: linear-gradient(45deg, #ff00ff, #00ffff, #ff00ff) border-box;
          border-radius: 20px;
        }
      `}</style>

      {/* Dynamic Background Elements */}
      <div className="absolute inset-0">
        <div className="absolute top-20 left-20 w-96 h-96 bg-gradient-to-r from-pink-500/20 to-purple-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-20 right-20 w-80 h-80 bg-gradient-to-r from-cyan-500/20 to-blue-500/20 rounded-full blur-3xl animate-pulse float-animation"></div>
        <div className="absolute top-1/2 left-1/3 w-72 h-72 bg-gradient-to-r from-yellow-500/15 to-orange-500/15 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-1/3 left-20 w-64 h-64 bg-gradient-to-r from-green-500/20 to-emerald-500/20 rounded-full blur-3xl animate-pulse"></div>
      </div>

      {/* Futuristic Header */}
      <div className="relative z-10 bg-gradient-to-r from-black/80 via-purple-900/80 to-black/80 backdrop-blur-xl border-b-2 border-cyan-400/50">
        <div className="max-w-7xl mx-auto px-6 py-16">
          <div className="text-center">
            <h1 className="text-8xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-6 tracking-tight glow-text">
              BRAND ANALYTICS
            </h1>
            <div className="text-6xl font-black bg-gradient-to-r from-yellow-400 via-orange-400 to-red-400 bg-clip-text text-transparent mb-8 tracking-tight">
              COMMAND CENTER
            </div>
            <p className="text-cyan-100 text-2xl font-bold mb-4">
               Real-time Brand Intelligence & Performance Analytics 
            </p>
            <div className="flex justify-center space-x-4">
              <div className="w-3 h-3 bg-cyan-400 rounded-full animate-pulse"></div>
              <div className="w-3 h-3 bg-purple-400 rounded-full animate-pulse" style={{animationDelay: '0.5s'}}></div>
              <div className="w-3 h-3 bg-pink-400 rounded-full animate-pulse" style={{animationDelay: '1s'}}></div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Dashboard Grid */}
      <div className="relative z-10 max-w-7xl mx-auto px-6 py-12">
        <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">

          {/* Trust Score Bar Chart */}
          <div className="chart-container rounded-3xl overflow-hidden glow-box lg:col-span-2">
            <div className="bg-gradient-to-r from-pink-600 via-purple-600 to-cyan-600 px-8 py-6">
              <h2 className="text-3xl font-bold text-white flex items-center glow-text">
                <div className="w-4 h-4 bg-white rounded-full mr-4 animate-pulse"></div>
                 TRUST SCORE MATRIX
              </h2>
              <p className="text-pink-100 text-lg mt-2 font-medium">Elite Brand Performance Rankings</p>
            </div>
            <div className="p-8 bg-gradient-to-br from-gray-900/50 to-purple-900/50">
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={trustData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    {GRADIENT_DEFS.map(grad => (
                      <linearGradient key={grad.id} id={grad.id} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={grad.start} stopOpacity={0.9}/>
                        <stop offset="100%" stopColor={grad.end} stopOpacity={0.6}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 14 }}
                    axisLine={{ stroke: '#cyan', strokeWidth: 2 }}
                    tickLine={{ stroke: '#cyan' }}
                  />
                  <YAxis 
                    domain={[0, 100]} 
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 14 }}
                    axisLine={{ stroke: '#cyan', strokeWidth: 2 }}
                    tickLine={{ stroke: '#cyan' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Bar dataKey="avg_trust_score" radius={[15, 15, 0, 0]} animationDuration={1500}>
                    {trustData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={`url(#${GRADIENT_DEFS[index % GRADIENT_DEFS.length].id})`} />
                    ))}
                  </Bar>
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Alert Level Pie Chart */}
          <div className="chart-container rounded-3xl overflow-hidden glow-box">
            <div className="bg-gradient-to-r from-yellow-600 via-orange-600 to-red-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center glow-text">
                <div className="w-4 h-4 bg-white rounded-full mr-4 animate-pulse"></div>
                 ALERT ZONES
              </h2>
              <p className="text-yellow-100 text-sm mt-2 font-medium">Threat Level Distribution</p>
            </div>
            <div className="p-8 bg-gradient-to-br from-gray-900/50 to-orange-900/50">
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <defs>
                    {NEON_COLORS.map((color, index) => (
                      <linearGradient key={`pieGrad-${index}`} id={`pieGrad-${index}`} x1="0" y1="0" x2="1" y2="1">
                        <stop offset="0%" stopColor={color} stopOpacity={0.8}/>
                        <stop offset="100%" stopColor={color} stopOpacity={0.4}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <Pie
                    data={alertLevelCounts}
                    dataKey="value"
                    nameKey="name"
                    cx="50%"
                    cy="50%"
                    outerRadius={100}
                    innerRadius={40}
                    paddingAngle={5}
                    animationDuration={1500}
                  >
                    {alertLevelCounts.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={`url(#pieGrad-${index})`} stroke={NEON_COLORS[index]} strokeWidth={2} />
                    ))}
                  </Pie>
                  <Tooltip content={<CustomTooltip />} />
                  <Legend wrapperStyle={{ color: '#ffffff', fontWeight: 'bold' }} />
                </PieChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Scatter Chart */}
          <div className="chart-container rounded-3xl overflow-hidden glow-box lg:col-span-2">
            <div className="bg-gradient-to-r from-green-600 via-teal-600 to-cyan-600 px-8 py-6">
              <h2 className="text-3xl font-bold text-white flex items-center glow-text">
                <div className="w-4 h-4 bg-white rounded-full mr-4 animate-pulse"></div>
                 INFLUENCE MATRIX
              </h2>
              <p className="text-green-100 text-lg mt-2 font-medium">Brand Visibility vs Trust Correlation</p>
            </div>
            <div className="p-8 bg-gradient-to-br from-gray-900/50 to-teal-900/50">
              <ResponsiveContainer width="100%" height={400}>
                <ScatterChart margin={{ top: 20, right: 20, bottom: 40, left: 20 }}>
                  <defs>
                    {NEON_COLORS.map((color, index) => (
                      <radialGradient key={`scatterGrad-${index}`} id={`scatterGrad-${index}`}>
                        <stop offset="0%" stopColor={color} stopOpacity={0.8}/>
                        <stop offset="100%" stopColor={color} stopOpacity={0.3}/>
                      </radialGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis
                    type="number"
                    dataKey="mention_count"
                    name="Mentions"
                    tick={{ fill: '#ffffff', fontSize: 14, fontWeight: 'bold' }}
                    axisLine={{ stroke: '#00ffff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#00ffff' }}
                    label={{ value: 'Mention Count', position: 'bottom', fill: '#00ffff', fontWeight: 'bold' }}
                    domain={['dataMin - 100', 'dataMax + 100']}
                  />
                  <YAxis
                    type="number"
                    dataKey="avg_trust_score"
                    name="Trust Score"
                    tick={{ fill: '#ffffff', fontSize: 14, fontWeight: 'bold' }}
                    axisLine={{ stroke: '#00ffff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#00ffff' }}
                    label={{ value: 'Trust Score', angle: -90, position: 'insideLeft', fill: '#00ffff', fontWeight: 'bold' }}
                    domain={[0, 100]}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Scatter name="Brands" data={scatterData} fill="#ec4899">
                    {scatterData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={`url(#scatterGrad-${index % NEON_COLORS.length})`} />
                    ))}
                  </Scatter>
                </ScatterChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Stacked Bar Chart */}
          <div className="chart-container rounded-3xl overflow-hidden glow-box">
            <div className="bg-gradient-to-r from-purple-600 via-pink-600 to-red-600 px-8 py-6">
              <h2 className="text-2xl font-bold text-white flex items-center glow-text">
                <div className="w-4 h-4 bg-white rounded-full mr-4 animate-pulse"></div>
                 THREAT LEVELS
              </h2>
              <p className="text-purple-100 text-sm mt-2 font-medium">Alert Distribution by Brand</p>
            </div>
            <div className="p-8 bg-gradient-to-br from-gray-900/50 to-pink-900/50">
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={stackedBarData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis 
                    dataKey="brand" 
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 12 }}
                    axisLine={{ stroke: '#ff00ff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#ff00ff' }}
                  />
                  <YAxis 
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 12 }}
                    axisLine={{ stroke: '#ff00ff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#ff00ff' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend verticalAlign="top" height={36} wrapperStyle={{ fontWeight: 'bold', color: '#ffffff' }} />
                  {alertLevels.map((level, idx) => (
                    <Bar
                      key={level}
                      dataKey={level}
                      stackId="a"
                      fill={NEON_COLORS[idx % NEON_COLORS.length]}
                      radius={idx === alertLevels.length - 1 ? [15, 15, 0, 0] : 0}
                      animationDuration={1500}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Trust Score Trend - Full Width */}
          <div className="chart-container rounded-3xl overflow-hidden glow-box lg:col-span-2 xl:col-span-3">
            <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 px-8 py-6">
              <h2 className="text-4xl font-bold text-white flex items-center glow-text">
                <div className="w-4 h-4 bg-white rounded-full mr-4 animate-pulse"></div>
                 TEMPORAL INTELLIGENCE GRID
              </h2>
              <p className="text-indigo-100 text-xl mt-2 font-medium">Trust Score Evolution Timeline</p>
            </div>
            <div className="p-8 bg-gradient-to-br from-gray-900/50 to-indigo-900/50">
              <ResponsiveContainer width="100%" height={500}>
                <AreaChart data={lineChartData} margin={{ top: 20, right: 30, left: 20, bottom: 40 }}>
                  <defs>
                    {uniqueBrands.map((brand, idx) => (
                      <linearGradient key={`areaGrad-${brand}`} id={`areaGrad-${brand}`} x1="0" y1="0" x2="0" y2="1">
                        <stop offset="0%" stopColor={NEON_COLORS[idx % NEON_COLORS.length]} stopOpacity={0.6}/>
                        <stop offset="100%" stopColor={NEON_COLORS[idx % NEON_COLORS.length]} stopOpacity={0.1}/>
                      </linearGradient>
                    ))}
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#ffffff20" />
                  <XAxis
                    dataKey="date"
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 14 }}
                    axisLine={{ stroke: '#00ffff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#00ffff' }}
                    tickFormatter={date => {
                      try {
                        const parsedDate = parseISO(date);
                        return isValid(parsedDate) ? format(parsedDate, 'MMM d') : new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
                      } catch (e) {
                        return new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
                      }
                    }}
                  />
                  <YAxis
                    domain={[0, 100]}
                    tick={{ fill: '#ffffff', fontWeight: 'bold', fontSize: 14 }}
                    axisLine={{ stroke: '#00ffff', strokeWidth: 2 }}
                    tickLine={{ stroke: '#00ffff' }}
                  />
                  <Tooltip content={<CustomTooltip />} />
                  <Legend verticalAlign="top" height={36} wrapperStyle={{ fontWeight: 'bold', color: '#ffffff' }} />
                  {uniqueBrands.map((brand, idx) => (
                    <Area
                      key={brand}
                      type="monotone"
                      dataKey={brand}
                      stroke={NEON_COLORS[idx % NEON_COLORS.length]}
                      fill={`url(#areaGrad-${brand})`}
                      strokeWidth={3}
                      connectNulls
                      animationDuration={2000}
                    />
                  ))}
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Futuristic Stats Panel */}
        <div className="mt-16 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <div className="chart-container rounded-3xl p-8 text-center glow-box hover:scale-105 transition-transform duration-300">
            <div className="text-6xl font-black bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text text-transparent mb-4 glow-text">
              {brands.length}
            </div>
            <div className="text-white font-bold text-2xl mb-2"> BRANDS</div>
            <div className="text-cyan-300 font-medium">Active Monitoring</div>
          </div>
          <div className="chart-container rounded-3xl p-8 text-center glow-box hover:scale-105 transition-transform duration-300">
            <div className="text-6xl font-black bg-gradient-to-r from-red-400 to-pink-400 bg-clip-text text-transparent mb-4 glow-text">
              {alerts.length}
            </div>
            <div className="text-white font-bold text-2xl mb-2"> ALERTS</div>
            <div className="text-pink-300 font-medium">System Notifications</div>
          </div>
          <div className="chart-container rounded-3xl p-8 text-center glow-box hover:scale-105 transition-transform duration-300">
            <div className="text-6xl font-black bg-gradient-to-r from-green-400 to-emerald-400 bg-clip-text text-transparent mb-4 glow-text">
              {brands.length > 0 ? Math.round(brands.reduce((sum, brand) => sum + brand.avg_trust_score, 0) / brands.length) : 0}
            </div>
            <div className="text-white font-bold text-2xl mb-2"> TRUST</div>
            <div className="text-green-300 font-medium">Global Average</div>
          </div>
          <div className="chart-container rounded-3xl p-8 text-center glow-box hover:scale-105 transition-transform duration-300">
            <div className="text-6xl font-black bg-gradient-to-r from-yellow-400 to-orange-400 bg-clip-text text-transparent mb-4 glow-text">
              {brands.length > 0 ? Math.round(brands.reduce((sum, brand) => sum + brand.mention_count, 0) / 1000) : 0}K
            </div>
            <div className="text-white font-bold text-2xl mb-2"> MENTIONS</div>
            <div className="text-yellow-300 font-medium">Total Engagement</div>
          </div>
        </div>
      </div>
    </div>
  );
}
