import React, { useEffect, useState } from 'react';
import axios from 'axios';
import dayjs from 'dayjs';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, Legend,
  ScatterChart, Scatter, ZAxis
} from 'recharts';

const COLORS = ['#60A5FA', '#F87171', '#34D399', '#FBBF24', '#A78BFA', '#F472B6'];

export default function FullDashboard() {
  const [brands, setBrands] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [biasTypes, setBiasTypes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5001/api/brands')
      .then(res => setBrands(res.data))
      .catch(console.error);

    axios.get('http://localhost:5001/api/alerts?limit=100')
      .then(res => {
        setAlerts(res.data);
        const types = [...new Set(res.data.map(a => a.bias_type))];
        setBiasTypes(types);
      })
      .catch(console.error);
  }, []);

  // ---------- CHART 1: Ranked Horizontal Bar for Trust ----------
  const rankedBrands = [...brands].sort((a, b) => b.avg_trust_score - a.avg_trust_score);

  const AvgTrustBarChart = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">🔹 Avg Trust Score (Ranked)</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart layout="vertical" data={rankedBrands}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis type="number" domain={[0, 100]} />
          <YAxis type="category" dataKey="brand" width={120} />
          <Tooltip />
          <Bar dataKey="avg_trust_score" fill="#60A5FA" radius={[0, 6, 6, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );

  // ---------- CHART 2: Mentions vs Trust Scatter ----------
  const TrustMentionsScatter = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">🔹 Mentions vs Trust Score</h2>
      <ResponsiveContainer width="100%" height={300}>
        <ScatterChart>
          <CartesianGrid />
          <XAxis dataKey="mention_count" name="Mentions" />
          <YAxis dataKey="avg_trust_score" name="Trust Score" domain={[0, 100]} />
          <Tooltip cursor={{ strokeDasharray: '3 3' }} />
          <Scatter data={brands} fill="#A78BFA" name="Brands" />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );

  // ---------- CHART 3: Stacked Bias Count Per Brand ----------
  const biasByBrand = {};
  alerts.forEach(({ brand, bias_type }) => {
    biasByBrand[brand] = biasByBrand[brand] || {};
    biasByBrand[brand][bias_type] = (biasByBrand[brand][bias_type] || 0) + 1;
  });

  const stackedData = Object.entries(biasByBrand).map(([brand, biases]) => ({
    brand,
    ...biases,
  }));

  const StackedBiasBarChart = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md overflow-x-auto">
      <h2 className="text-xl font-semibold mb-2">🔹 Bias Type Count per Brand</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={stackedData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="brand" />
          <YAxis />
          <Tooltip />
          <Legend />
          {biasTypes.map((type, idx) => (
            <Bar key={type} dataKey={type} stackId="a" fill={COLORS[idx % COLORS.length]} />
          ))}
        </BarChart>
      </ResponsiveContainer>
    </div>
  );

  // ---------- CHART 4: Bubble Timeline of Last Mentions ----------
  const bubbleData = brands.map(b => ({
    brand: b.brand,
    date: new Date(b.last_mention).getTime(),
    mention_count: b.mention_count,
  }));

  const LastMentionBubbleChart = () => (
    <div className="bg-white p-4 rounded-2xl shadow-md">
      <h2 className="text-xl font-semibold mb-2">🔹 Last Mention Timeline</h2>
      <ResponsiveContainer width="100%" height={300}>
        <ScatterChart>
          <CartesianGrid />
          <XAxis
            type="number"
            dataKey="date"
            name="Last Mention"
            domain={['auto', 'auto']}
            tickFormatter={tick => dayjs(tick).format('MMM D')}
          />
          <YAxis dataKey="mention_count" name="Mentions" />
          <ZAxis dataKey="mention_count" range={[60, 200]} />
          <Tooltip
            formatter={(value, name) => (name === 'date' ? dayjs(value).format('MMM D, YYYY') : value)}
            labelFormatter={() => ''}
          />
          <Scatter data={bubbleData} fill="#34D399" name="Brands" />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 p-6 bg-gray-50 min-h-screen">
      <AvgTrustBarChart />
      <TrustMentionsScatter />
      <StackedBiasBarChart />
      <LastMentionBubbleChart />
    </div>
  );
}
